const m=document.getElementById("days"),u=document.getElementById("hours"),r=document.getElementById("minutes"),a=document.getElementById("seconds"),l=new Date("2025-11-04T00:00:00");function e(){const n=new Date().getTime(),t=l-n,o=Math.floor(t/(1e3*60*60*24)),s=Math.floor(t%(1e3*60*60*24)/(1e3*60*60)),c=Math.floor(t%(1e3*60*60)/(1e3*60)),d=Math.floor(t%(1e3*60)/1e3);m.textContent=o,u.textContent=s,r.textContent=c,a.textContent=d}setInterval(e,1e3);e();
